import axios from "axios"
import { useEffect, useState } from "react"
import "./Categories.css"
export const Categories = () =>{

    const [categories ,setCategories] = useState([])
    const [numberofCategoryToShow,setnumberofCategoryToShow]=useState(0);

    useEffect(()=>{
        (async ()=>{
        try{
        const {data} = await axios.get("https://travelapp-backend-cdeh.onrender.com/api/category")
        const categoriesToShow = data.slice(0,10);
        console.log(data)
        setCategories(categoriesToShow)
        }catch(err){
        console.log(err)
        }
    })()
 } ,[])

    return (
        <section className="categories gap-large d-flex align center gap cursor-pointer">
            {categories && categories.map(({category})=><span key={category._id}>{category}</span>)}
        </section>
    )
}
