export const HotelImages = ({ singleHotel }) => {
  const { image, imageArr } = singleHotel;

  return (
    <div className="flex flex-col md:flex-row gap-2 md:gap-4">
      {/* Main Image on the Left */}
      <div className="flex-shrink-0 h-[20rem] md:h-[25rem] md:w-2/3">
        <img
          className="w-full h-full object-cover rounded-lg"
          src={image}
          alt="hotel"
        />
      </div>

      {/* Smaller Images Grid on the Right */}
      <div className="grid grid-cols-2 gap-2 md:w-1/3">
        {imageArr &&
          imageArr.map((img, index) => (
            <div
              key={index}
              className="relative h-28 md:h-32 overflow-hidden rounded-lg"
            >
              <img
                className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                src={img}
                alt="hotel"
              />
            </div>
          ))}
      </div>
    </div>
  );
};
