import { useFilter } from "../../../context/filter-context";

export const FreeCancel = () => {
    const { filterDispatch, isCancelable } = useFilter();

    const handleCancelChange = (event) => {
        filterDispatch({
            type: "CANCELABLE",
            payload: event.target.checked,
        });
    };

    return (
        <div className="filter-container mb-4">
            <div className="flex items-center gap-4">
                <span className="text-lg font-medium">Free Cancellation</span>
                <label className="relative inline-flex items-center cursor-pointer">
                    <input
                        type="checkbox"
                        onChange={handleCancelChange}
                        value={isCancelable}
                        checked={isCancelable}
                        className="sr-only" // Hides the checkbox input
                    />
                    <span className="block w-10 h-6 bg-gray-300 rounded-full transition duration-200 ease-in-out">
                        <span className={`absolute left-0 w-4 h-4 bg-white rounded-full shadow transform transition duration-200 ease-in-out ${isCancelable ? "translate-x-full bg-blue-500" : ""}`}></span>
                    </span>
                </label>
            </div>
        </div>
    );
};
