import { createContext,useContext, useReducer } from "react";
import { dateReducer } from "../reducer/date-reducer";

const DateContext = createContext();
const initialValue = {
    checkinDate : null,
    checkOutDate : null,
    isSearchModalOpen : false
}
const DateProvider = ({children}) =>{

    const [{checkinDate,checkOutDate,isSearchModalOpen},dateReducer] = useReducer(dateReducer,initialValue)
    return(
        <DateContext.Provider value={{checkinDate,checkOutDate,isSearchModalOpen}}>
            {children}
        </DateContext.Provider>
    )
}

const useDate = () => useContext(DateContext);

export {useDate,DateProvider}
