import DatePicker from "react-datepicker"
import "react-datepicker/dist/react-datepicker.css";

import "./DateSelector.css"
import "./DateSelector.css"
import { useDate } from "../../context/date-context";
import { useState } from "react"
export const DateSelector =({placeholder,checkInType})=>{
    const {checkinDate,checkOutDate,dateDispatch} = useDate()

    const handleDateChange =(date) =>{
        dateDispatch({
            type: checkInType === "in" ? "CHECK_IN" : "CHECK_OUT" ,
            payload: date,
        })
    }

    const [currentDate,setCurrentDate] = useState(new Date())
    return <DatePicker selected={checkInType === "in" ? checkinDate : checkOutDate} onChange={date=>handleDateChange(date)} className="search-dest input" dateFormat = "dd/mm/yyyy" placeholder="Check In" closeOnScroll={true}/>
}
