export const HotelImages = ({ singleHotel }) => {
  const { image, imageArr } = singleHotel;

  return (
    <div className="flex flex-col md:flex-row gap-2 md:gap-4">
      <div className="flex-shrink-0 h-[25rem] md:h-[30rem] md:w-2/3"> 
        <img
          className="w-full h-full object-cover rounded-lg" 
          src={image}
          alt="hotel"
        />
      </div>
      <div className="grid grid-cols-2 gap-x-2 gap-y-0 md:w-1/3"> {/* Set vertical gap to 0 */}
        {imageArr &&
          imageArr.map((img, index) => (
            <div key={index} className="relative "> 
              <img
                className="w-full h-32 md:h-40 object-cover rounded-lg transition-transform duration-300 group-hover:scale-105" // Scale on hover
                src={img}
                alt="hotel"
              />
            </div>
          ))}
      </div>
    </div>
  );
};
