import "./Auth.css"
import { useAuth } from "../../context/auth-context"
import { eventWrapper } from "@testing-library/user-event/dist/utils"

export const AuthSignUp = () =>{
    const {name,number,email,password,authDispatch} = useAuth()

    const handleNumberChange = (e) =>{
        authDispatch({
            type:"NUMBER",
            payload:e.target.value
        })
    }
    const handleNameChange = (e) =>{
        authDispatch({
            type:"NAME",
            payload:e.target.value
        })
    }
    const handleEmailChange = (e) =>{
        authDispatch({
            type:"EMAIL",
            payload:e.target.value
        })
    }
    const handlePasswordChange = (e) =>{
        authDispatch({
            type:"PASSWORD",
            payload:e.target.value
        })
    }
    const handleConfirmPasswordChange = (e) =>{
        authDispatch({
            type:"CONFIRM_PASSWORD",
            payload:e.target.value
        })
    }
    return(
        <div className="auth-container">
        <form >
          <div className="d-flex direction-column lb-in-container">
            <label className="auth-label">
              Mobile Number <span className="asterisk">*</span>{" "}
            </label>
            <input
              type="number"
              className="auth-input"
              maxLength="10"
              placeholder="Enter Mobile Number"
              required
              onChange={handleNumberChange(event)}
            />
          </div>
          <div className="d-flex direction-column lb-in-container">
            <label className="auth-label">
              Name <span className="asterisk">*</span>{" "}
            </label>
            <input
              className="auth-input"
              placeholder="Enter Name"
              required
              onChange={handleNameChange(event)}
            />
          </div>
          <div className="d-flex direction-column lb-in-container">
            <label className="auth-label">
              Email <span className="asterisk">*</span>{" "}
            </label>
            <input
              className="auth-input"
              placeholder="Enter Email"
              type="email"
              required
              onChange={handleEmailChange(event)}
            />
          </div>
          <div className="d-flex direction-column lb-in-container">
            <label className="auth-label">
              Password <span className="asterisk">*</span>{" "}
            </label>
            <input
           
              className="auth-input"
              placeholder="Enter Password"
              type="password"
              required
              onChange={handlePasswordChange(event)}
            />
          </div>
          <div className="d-flex direction-column lb-in-container">
            <label className="auth-label">
              Confirm Password <span className="asterisk">*</span>{" "}
            </label>
            <input
             
              className="auth-input"
              placeholder="Enter Password"
              type="password"
              required
              onChange={handleConfirmPasswordChange(event)}
           
            />
          </div>
          <div>
            <button className="button btn-primary btn-login cursor">
              Submit
            </button>
          </div>
        </form>
      </div>
    )
}
