import React, { useState, useEffect, useMemo } from "react";
import { useDate } from "../context/date-context";
import { useCategory } from "../context/category-context";
import { DateSelector } from "./DateSelector/DateSelector";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export const HomeSelection = () => {
    const { dateDispatch, destination, guests, isSearchResultOpen } = useDate();
    const { hotelCategory } = useCategory();
    const [hotels, setHotels] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchHotels = async () => {
            try {
                const { data } = await axios.get(
                    `https://travelapp-backend-3hjw.onrender.com/api/hotels?category=${hotelCategory}`
                );
                setHotels(data);
            } catch (err) {
                console.error("Error fetching hotels:", err);
                setError("Failed to load hotels. Please try again later.");
            }
        };
        fetchHotels();
    }, [hotelCategory]);

    const handleDestinationChange = (e) => {
        dateDispatch({
            type: "DESTINATION",
            payload: e.target.value,
        });
    };

    const handleGuestChange = (e) => {
        dateDispatch({
            type: "GUESTS",
            payload: e.target.value,
        });
    };

    const handleSearchResultClick = (address) => {
        dateDispatch({
            type: "DESTINATION",
            payload: address,
        });
        dateDispatch({
            type: "CLOSE_SEARCH_MODAL",
        });
    };

    const destinationOptions = useMemo(
        () =>
            hotels.filter(({ address, city, state, country }) =>
                [address, city, state, country].some((field) =>
                    field.toLowerCase().includes(destination?.toLowerCase())
                )
            ),
        [hotels, destination]
    );

    const handleDestinationFocus = () => {
        dateDispatch({ type: "SHOW_SEARCH_RESULT" });
    };

    const handleSearchButtonClick = () => {
        dateDispatch({ type: "CLOSE_SEARCH_MODAL" });
        navigate(`/hotels/${destination}`);
    };

    return (
        <div className="destination-options border border-white bg-background rounded-lg p-4 w-96 mt-4 relative shadow-2xl font-manrope flex flex-col">
            <div className="flex flex-col space-y-4">
                <div>
                    <label className="label font-bold text-xs">LOCATION</label>
                    <input
                        value={destination}
                        onChange={handleDestinationChange}
                        onFocus={handleDestinationFocus}
                        className="input search-dest p-4 border rounded w-full"
                        placeholder="Search Destination"
                        autoFocus
                    />
                </div>
                <div className="flex justify-between items-center">
                    <label className="label">Check in</label>
                    <DateSelector checkInType="in" />
                </div>
                <div className="flex justify-between items-center">
                    <label className="label">Check out</label>
                    <DateSelector checkInType="out" />
                </div>
                <div>
                    <label className="label">No. of Guests</label>
                    <input
                        value={guests}
                        className="input search-dest p-4 border rounded w-full"
                        placeholder="Add guests"
                        onChange={handleGuestChange}
                    />
                </div>
                <div
                    className="search-container flex items-center justify-center cursor-pointer bg-primary text-white p-2 rounded"
                    onClick={handleSearchButtonClick}
                >
                    <span className="material-icons-outlined">search</span>
                    <span className="ml-2">Search</span>
                </div>
            </div>

            {error && <p className="text-red-500 mt-2">{error}</p>}

            {isSearchResultOpen && destination?.trim() && (
                <div className="search-result-container bg-background text-primary mt-4 max-h-40 overflow-y-auto rounded shadow-md absolute w-full">
                    {destinationOptions.map(({ address, city }, index) => (
                        <p
                            key={index}
                            className="cursor-pointer hover:bg-gray-200 px-2 py-1"
                            onClick={() => handleSearchResultClick(address)}
                        >
                            {address}, {city}
                        </p>
                    ))}
                </div>
            )}
        </div>
    );
};
