import { createContext,useContext, useReducer } from "react";
import { dateReducer } from "../reducer/date-reducer";


const initialValue = {
    destination:"",
    checkinDate : null,
    checkOutDate : null,
    isSearchModalOpen : false
}
const DateContext = createContext(initialValue);
const DateProvider = ({children}) =>{

    const [{destination,checkinDate,checkOutDate,isSearchModalOpen},dateDispatch] = useReducer(dateReducer,initialValue)
    return(
        <DateContext.Provider value={{destination,checkinDate,checkOutDate,isSearchModalOpen,dateDispatch}}>
            {children}
        </DateContext.Provider>
    )
}

const useDate = () => useContext(DateContext);

export {useDate,DateProvider}
