import DatePicker from "react-datepicker"
import "react-datepicker/dist/react-datepicker.css";

import "./DateSelector.css"
import { useState } from "react";

export const DateSelector =({checkInType})=>{
    const [currentDate , setCurrentData ] = useState(new Date())

    const handleDateChange =(date) =>{
        dateDispatch({
            type: checkInType === "in" ? "CHECK_IN" : "CHECK_OUT" ,
            payload: date,
        })
    }

    return <DatePicker selected={currentDate} onChange={date=>handleDateChange(date)} className="search-dest input" dateFormat = "dd/mm/yyyy" placeholderText="Check In" closeOnScroll={true}/>
}
