import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDate } from "../../context/date-context";
import { useCategory } from "../../context/category-context";
import { DateSelector } from "../DateSelector/DateSelector";
import { useNavigate } from "react-router-dom";

export const SearchStayWithDate = () => {
  const { dateDispatch, destination, guests, isSearchResultOpen } = useDate();
  const { hotelCategory } = useCategory();
  const [hotels, setHotels] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const { data } = await axios.get(
          `https://travelapp-backend-cdeh.onrender.com/api/hotels?category=${hotelCategory}`
        );
        setHotels(data);
      } catch (err) {
        console.error("Error fetching hotels:", err);
      }
    })();
  }, [hotelCategory]);

  const handleDestinationChange = (e) => {
    dateDispatch({
      type: "DESTINATION",
      payload: e.target.value,
    });
  };

  const handleGuestChange = (e) => {
    dateDispatch({
      type: "GUESTS",
      payload: e.target.value,
    });
  };

  const handleSearchResultClick = (address) => {
    dateDispatch({
      type: "DESTINATION",
      payload: address,
    });
  };

  const destinationOptions = hotels.filter(({ address, city, state, country }) =>
    address.toLowerCase().includes(destination.toLowerCase()) ||
    city.toLowerCase().includes(destination.toLowerCase()) ||
    state.toLowerCase().includes(destination.toLowerCase()) ||
    country.toLowerCase().includes(destination.toLowerCase())
  );

  const handleDestinationFocus = () => {
    dateDispatch({
      type: "SHOW_SEARCH_RESULT",
    });
  };

  const handleSearchButtonClick = () => {
    dateDispatch({
      type: "CLOSE_SEARCH_MODAL",
    });
    navigate(`/hotels/${destination}`);
  };

  const handleCoseModal = () => {
    dateDispatch({
      type: "OPEN_SEARCH_MODAL",
    });
  };

  return (
    <div className="destination-container">
      <div className="destination-options flex flex-col sm:flex-row items-center relative">
        <div className="location-container flex-1">
          <label className="label block text-sm font-medium">Where</label>
          <input
            value={destination}
            onChange={handleDestinationChange}
            onFocus={handleDestinationFocus}
            className="input search-dest border rounded-md p-2 w-full"
            placeholder="Search Destination"
            autoFocus
          />
        </div>
        <div className="location-container flex-1">
          <label className="label block text-sm font-medium">Check in</label>
          <DateSelector checkInType="in" />
        </div>
        <div className="location-container flex-1">
          <label className="label block text-sm font-medium">Check out</label>
          <DateSelector checkInType="out" />
        </div>
        <div className="location-container flex-1">
          <label className="label block text-sm font-medium">No. of Guests</label>
          <input
            value={guests}
            className="input search-dest border rounded-md p-2 w-full"
            placeholder="Add guests"
            onChange={handleGuestChange}
          />
        </div>
        <div
          className="search-container flex items-center cursor-pointer px-4 py-2 bg-blue-500 text-white rounded-md mt-2 sm:mt-0 sm:ml-2"
          onClick={handleSearchButtonClick}
        >
          <span className="material-icons-outlined">search</span>
          <span className="ml-2">Search</span>
        </div>
        <button
          onClick={handleCoseModal}
          className="button absolute right-2 top-2 p-2 bg-red-500 text-white rounded-full"
        >
          <span className="highlight material-icons-outlined">
            highlight_off
          </span>
        </button>
      </div>
      {isSearchResultOpen && (
        <div className="search-result-container absolute bg-white shadow-md z-10">
          {destinationOptions &&
            destinationOptions.map(({ address, city }) => (
              <p
                className="p cursor-pointer hover:bg-gray-100 p-2"
                onClick={() => handleSearchResultClick(address)}
                key={address}
              >
                {address}, {city}
              </p>
            ))}
        </div>
      )}
    </div>
  );
};
