export const HotelImages = ({ singleHotel }) => {
  const { image, imageArr } = singleHotel;

  return (
    <div className="flex flex-col md:flex-row gap-2 md:gap-4">
      <div className="flex-shrink-0 h-[25rem] md:h-[30rem] md:w-2/3"> {/* Primary Image */}
        <img
          className="w-full h-full object-cover rounded-lg" // Added rounded corners for better aesthetics
          src={image}
          alt="hotel"
        />
      </div>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-2 md:w-1/3"> {/* Thumbnail Images in a Grid */}
        {imageArr &&
          imageArr.map((img, index) => (
            <img
              key={index} // Using index as key as image URL might not be unique
              className="w-full h-24 object-cover rounded-lg" // Full width and rounded corners
              src={img}
              alt="hotel"
            />
          ))}
      </div>
    </div>
  );
};
